import React, { useEffect, useState } from 'react';
import axios from 'axios';

const LocationButton = () => {
  const [storeData, setStoreData] = useState([]);
  const [regions, setRegions] = useState([]);
  const [distributors, setDistributors] = useState([]);
  const [spvs, setSpvs] = useState([]);
  const [selectedRegion, setSelectedRegion] = useState('');
  const [selectedDistributor, setSelectedDistributor] = useState('');
  const [selectedSPV, setSelectedSPV] = useState('');
  const [filteredStores, setFilteredStores] = useState([]);
  const [selectedStore, setSelectedStore] = useState([]);

  useEffect(() => {
    axios.get('http://localhost:4000/api/store-data')
      .then((res) => {
        setStoreData(res.data);

        const uniqueRegions = [...new Set(res.data.map(row => row.region))];
        setRegions(uniqueRegions);

        const uniqueDistributors = [...new Set(res.data.map(row => row.distributor))];
        setDistributors(uniqueDistributors);

        const uniqueSpvs = [...new Set(res.data.map(row => row.spv))];
        setSpvs(uniqueSpvs);
      });
  }, []);

  const handleSPVChange = (spv) => {
    setSelectedSPV(spv);

    // Filter regions and distributors based on the selected SPV
    const filteredRegion = storeData.filter(row => row.spv === spv);
    const uniqueRegions = [...new Set(filteredRegion.map(row => row.region))];
    setRegions(uniqueRegions);

    const filteredDistributors = storeData.filter(row => row.spv === spv);
    const uniqueDistributors = [...new Set(filteredDistributors.map(row => row.distributor))];
    setDistributors(uniqueDistributors);

    // Filter stores by SPV, region, and distributor
    filterStores(selectedRegion, selectedDistributor, spv);
  };

  const handleRegionChange = (region) => {
    setSelectedRegion(region);
    filterStores(region, selectedDistributor, selectedSPV);
  };

  const handleDistributorChange = (distributor) => {
    setSelectedDistributor(distributor);
    filterStores(selectedRegion, distributor, selectedSPV);
  };

  const filterStores = (region, distributor, spv) => {
    setFilteredStores(storeData.filter(row => 
      (region ? row.region === region : true) && 
      (distributor ? row.distributor === distributor : true) && 
      (spv ? row.spv === spv : true)
    ));
    setSelectedStore(null);
  };

  const handleStoreChange = (storeId) => {
    const store = filteredStores.find(s => s.store_id === storeId);
    setSelectedStore(store);
  };

  const sendLocation = () => {
    if (!selectedStore) return alert('Select a store first');

    if ('geolocation' in navigator) {
      navigator.geolocation.getCurrentPosition(
        async (pos) => {
          const body = {
            latitude: pos.coords.latitude,
            longitude: pos.coords.longitude,
            timestamp: new Date().toISOString(),
            ...selectedStore
          };

          try {
            await axios.post('http://localhost:4000/api/location', body);
            alert('Location sent!');
          } catch (err) {
            alert('Failed to send location');
          }
        },
        () => alert('Error getting location')
      );
    } else {
      alert('Geolocation not supported');
    }
  };

  return (
    <div className="p-4 bg-white shadow-lg rounded-xl space-y-4 w-full max-w-md">
      <h2 className="text-xl font-bold">Send My Location</h2>

      <select 
        value={selectedSPV} 
        onChange={(e) => handleSPVChange(e.target.value)} 
        className="w-full p-2 border"
      >
        <option value="">Select SPV</option>
        {spvs.map(spv => <option key={spv} value={spv}>{spv}</option>)}
      </select>

      <select 
        value={selectedRegion} 
        onChange={(e) => handleRegionChange(e.target.value)} 
        className="w-full p-2 border"
      >
        <option value="">Select Region</option>
        {regions.map(region => <option key={region} value={region}>{region}</option>)}
      </select>

      <select 
        value={selectedDistributor} 
        onChange={(e) => handleDistributorChange(e.target.value)} 
        className="w-full p-2 border"
      >
        <option value="">Select Distributor</option>
        {distributors.map(distributor => <option key={distributor} value={distributor}>{distributor}</option>)}
      </select>

      <select 
        value={selectedStore?.store_id || ''} 
        onChange={(e) => handleStoreChange(e.target.value)} 
        className="w-full p-2 border"
      >
        <option value="">Select Store</option>
        {filteredStores.map(s => (
          <option key={s.store_id} value={s.store_id}>
            {`${s.store_id} - ${s.store_name}`}
          </option>
        ))}
      </select>

      <button 
        onClick={sendLocation} 
        className="bg-blue-600 text-white w-full p-2 rounded hover:bg-blue-700"
      >
        Send Location
      </button>
    </div>
  );
};

export default LocationButton;
